// Clock with large digits using the "Anton" bold font
Graphics.prototype.setFontAnton = function(scale) {
    g.setFontCustom(atob(""), 46, atob("EiAnGicnJycnJycnEw=="), 78 + (scale << 8) + (1 << 16));
  };
  
  { // must be inside our own scope here so that when we are unloaded everything disappears
    // we also define functions using 'let fn = function() {..}' for the same reason. function decls are global

  let drawTimeout;
  let syncing = false;
  let last_steps = 0;
  let writeNextLog =function() {
      var file = require("Storage").open("healthlog"+version+".csv","a");
      var arr2 = new ArrayBuffer(6); // an Int32 takes 4 bytes and Int16 takes 2 bytes
      var time = Math.floor(Date.now() / 1000);
      var steps = Bangle.getStepCount();
      var delta = steps - last_steps;
      last_steps = steps;
      view = new DataView(arr2);
      view.setUint32(0, time, false); // byteOffset = 0; litteEndian = false
      view.setUint16(4, delta, false);
      file.write(btoa(arr2));
  };
  
  let version = "6";
  
  let locale = require("locale");
  
  // Actually draw the watch face
  let draw = function() {
  
    if(!syncing){
      writeNextLog();
    }
    //open the file and write the data
  
    var x = g.getWidth() / 2;
    var y = g.getHeight() / 2;
    g.reset().clearRect(Bangle.appRect); // clear whole background (w/o widgets)
    var date = new Date();
    var timeStr = locale.time(date, 1); // Hour and minute
    g.setFontAlign(0, 0).setFont("Anton").drawString(timeStr, x, y);
    // Show date and day of week
    var dateStr = locale.date(date, 0).toUpperCase()+"\n"+
                  locale.dow(date, 0).toUpperCase();
    g.setFontAlign(0, 0).setFont("6x8", 2).drawString(dateStr, x, y+48);
  
    // queue next draw
    if (drawTimeout) clearTimeout(drawTimeout); //
    drawTimeout = setTimeout(function() {
      drawTimeout = undefined;
      draw();
    }, 60000 - (Date.now() % 60000));
  };
  
  
  let writePacket = function(arr){
      NRF.updateServices({
              0xABC1: {
                  0xABC2: {
                      value : arr,
                      notify: true
                  }
              }
      });
  };
  
  let sendData = function(storageFile){
  
    while(true){
        s = storageFile.read(128);
        if(s!=undefined){
            Bluetooth.write(s);
           
        }else{
            break;
        }
       
    }
    Bluetooth.write(10); 
    //   var dataLeft = true;
    //   s = storageFile.read(126);
    //   packet = "";
    //   if(s == undefined){
    //       packet = "0";
    //       dataLeft = false;
    //   }else if(s.length < 126){
    //       packet = "0" + s;
    //       dataLeft = false;
    //   }else{
    //       packet = "1" + s;
    //   }
    //   writePacket(packet);
  
    //   if(dataLeft){
    //       setTimeout(sendData, 20, storageFile);
    //   }
  };
  let handleIncomingPacket=function(b){
      if(b[0] == 1){
          syncing = true;
          //E.showAlert("syncing").then(draw); 
  
          
          //read the file
          var healthlog = require("Storage").open("healthlog"+version+".csv","r");
          sendData(healthlog);
          
          
      }else if(b[0] == 2){
          E.showAlert("deleting").then(draw);
          require("Storage").open("healthlog"+version+".csv","r").erase();
          Bluetooth.write("deleted storage")
      }else if(b[0] == 7){
        E.setConsole("Bluetooth");
      }
  };
  let setupServer = function(){
      NRF.on('disconnect', function(reason) { 
          //probably want to clean any lingering buffers up.
          syncing = false;
          Bangle.buzz();
      });
      NRF.setTxPower(8);
      NRF.setServices({
          0xABC1: {
              0xABC3: { //rx, handle data in onWRite
                  value: [0],
                  maxLen: 128,
                  writable: true,
                  onWrite : function(evt){
                      handleIncomingPacket(evt.data);
                  }
              }
          }
      }, { uart : true }); //uart: true is important, otherwise you can't do regular bangle things (must put in bootloader mode to fix)
      // Change the name that's advertised
      //NRF.setAdvertising({}, {name:"Bangle.js"});
  };
  
  E.setConsole(null, {force: true});
  Bluetooth.on('data', function(data) {
    if(data.charCodeAt(0) == 1){
        sendData(require("Storage").open("healthlog"+version+".csv","r"));
        Bluetooth.write("sent file" + require("Storage").open("healthlog"+version+".csv","r").getLength())
    }

  });
  setupServer();
  
  // Load widgets
  Bangle.loadWidgets();
  draw();
  setTimeout(Bangle.drawWidgets,0);
  }
  

